js:

var d = [];

/*--变量名别动--*/
const Color = "#19B89D";

var id = MY_URL.split('#')[1]

MY_URL = config.qm + id

var html = fetch(MY_URL, {
    headers: {
        'User-Agent': 'Mozilla/5.0 (Windows NT 10.0)',
        'Referer' : MY_URL
    }
})

var Icon_prefix = 'https://lanmeiguojiang.com/tubiao/q/';
var Label_set_icon = ['107.png', '18.png', '32.png'];
var doList = []

//标题
var title = pdfh(html, 'h1&&Text');
//封面图 一定不要加Referer
var des_pic = pd(html, '.banner_border_bg&&img&&src');
var conts=parseDom(html, '#chapterlistload&&Html').match(/<a[\s\S]*?<\/a>/g);
//剧情简介

var des_desc = pdfh(html, '.content&&Text').replace(/详情|介绍|简介/, '');
//文字描述<br>换行
var des_text =  pdfh(html, '.tip&&Text');

eval(JSON.parse(request("hiker://page/jx")).rule)

d.push({
    title: "‘‘’’<b><font color='#f8ecc9'>" + title + '<br>' + pdfh(html, '.subtitle&&Text') +  '</font></b>',
    desc: '‘‘’’<small><font color="#FA7298">' + des_text + '</font>' + '</small>',
    pic_url: des_pic,
    url: "hiker://page/download.view#noRecordHistory##noRefresh#?rule=本地资源管理",
    col_type: "text_center_1",
    extra: {
        chapterList: doList,
        info: {
            bookName: title,
            ruleName: MY_RULE.title,
            bookTopPic: des_pic,
            parseCode: downloadlazy
        },
        defaultView: "1"
    },
    col_type: 'movie_1_vertical_pic_blur'
});

//简介
var 简介全 = des_desc.replace("简介：", "").replace("介绍:", "");
var jj = getMyVar(getParam('url') + "简介收", "已收起");
var mztext = "&nbsp;&nbsp;&nbsp;&nbsp;<small>简介</small><br>"

var bttitle = '<font color="#098AC1">漫画简介  </font><small><small><font color="#f47983">简介收></font></small></small>'

var jjurl = $("#noLoading#").lazyRule((简介全, 收, 收内容, 当前URL) => {
    var jsbl = 当前URL + "简介收";
    var jj = getMyVar(当前URL + "简介收", "已收起");
    jj = jj == "已收起" ? "展开" : "已收起";
    putMyVar(jsbl, jj)
    updateItem({
        extra: {
            id: 当前URL + "_bt"
        },
        title: 收.replace("简介收", jj)
    })

    updateItem({
        extra: {
            id: 当前URL + "_mz"
        },
        title: jj == "已收起" ? 收内容.replace("简介", 简介全).substr(0, 75) + " ...<br>" : 收内容.replace("简介", 简介全)
    })
    return "hiker://empty"
}, 简介全, bttitle, mztext, getParam('url'))

d.push({
    title: bttitle.replace("简介收", jj),
    url: jjurl,
    col_type: "avatar",
    pic_url: Icon_prefix + Label_set_icon[0],
    extra: {
        id: getParam('url') + "_bt"
    }
});

d.push({
    title: jj == "已收起" ? mztext.replace("简介", 简介全).substr(0, 75) + " ...<br>" : mztext.replace("简介", 简介全),
    col_type: "rich_text",
    extra: {
        id: getParam('url') + "_mz"
    }
});





if (conts.length > 15) {
    var conts2 = [];
    for (var j = 0; j < conts.length; j++) {
        conts2.push({
            name: parseDomForHtml(conts[j], 'a&&Text'),
            id: parseDomForHtml(conts[j], 'a&&href').split('/')[2].split('.html')[0]
        })
    }
    eval("var conts = JSON.parse(fetch(config.qm+'/chapterlist' + id, {headers:{'X-Requested-With': 'XMLHttpRequest','Referer': MY_URL},method:'GET'})).data.list");
    conts.unshift.apply(conts, conts2);
    for (var i = conts.length - 1; i >= 0; i--) {
        let g = conts[i];
        d.push({
            title: g.name,
            url: getUrl().replace('#immersiveTheme##autoCache#', '') + g.id + '.html' + lazy
        })
        doList.push({
            title: g.name,
            url: getUrl().replace('#immersiveTheme##autoCache#', '') + g.id + '.html'
        });
    }
} else {
    for (var i = conts.length - 1; i >= 0; i--) {
        d.push({
            title: parseDomForHtml(conts[i], 'a&&Text'),
            url: parseDom(conts[i], 'a&&href') + lazy
        })
        doList.push({
            title: parseDomForHtml(conts[i], 'a&&Text'),
            url: parseDom(conts[i], 'a&&href')
        });
    }
}
setResult(d);

