function 解析() {
    var html = request(input, {
        headers: {
            "User-Agent": MOBILE_UA
        }
    });
    var pics = pdfa(html, ".acgn-reader-chapter__item-box&&img").map(h => pdfh(h, "img&&data-src||src"));
    if (/下一页/.test(html)) {
        for (var k = 2;; k++) {
            var html_tmp = request(input.replace(".html", "_" + k + ".html"), {
                headers: {
                    "User-Agent": MOBILE_UA
                }
            });
            pics = pics.concat(pdfa(html_tmp, ".acgn-reader-chapter__item-box&&img").map(h => pdfh(h, "img&&data-src||src")));
            if (!/下一页/.test(html_tmp)) {
                break;
            }
        }
    }

    return "pics://" + pics.join("@Referer=https://pic4.yemancomic.com/&&") + "@Referer=https://pic4.yemancomic.com/";
}
let lazy = $("").lazyRule(解析);
let downloadlazy = $.toString(解析);
//var all_list = pdfa(html, 'body&&.comic-chapters');
//line(all_list, ".caption&&Text", "mh-line");
//var line = parseInt(getVar("mh-line", "0"));
var clist = pdfa(html, ".bd||.view-win-list&&li");
chapter(标题, 封面图片, clist, 'a&&Text', 'a&&href', 1, true, downloadlazy, deleted);
setResult(d);

