function cover(标题, 作者, 分类, 状态, 章节, 更新, 简介,封面图片,true_url, hasLine) {
var 标题, 作者, 分类, 状态, 章节, 更新, 简介,封面图片,true_url, hasLine;
	try {
		d.push({
			title: '‘‘’’<small><font color="#FA7298">'+作者.slice(0,15)+"&nbsp;"+状态+'</font></small>'+'‘‘’’<small><font color="#B15BFF">'+"\n"+分类+'</font></small>'+'‘‘’’<small><font color="#FF359A">'+"\n"+章节+"&nbsp;"+更新+'</font></small>',
			desc:'‘‘’’<small>' +简介+ '</small>',
			pic_url:  封面图片,
			url: true_url,
			col_type: 'movie_1_vertical_pic_blur'
		});
		d.push({
			col_type: 'line_blank',
		});
		d.push({
			col_type: 'line',
		});
		if (hasLine) {
			item.extra = {
				lineVisible: false
			}
		}
		d.push(item);
	} catch (e) {}
}
function chapter(title,pic_url,chapterList, chapterTitle, chapterUrl, defaultOrder, hasLine,downloadlazy,deleted) {
	var list = chapterList;
	var original = [{
		url: MY_URL,
		order: defaultOrder
	}];
	!fileExist('hiker://files/rules/comic/comicLogFile.js') ? (writeFile('hiker://files/rules/comic/comicLogFile.js', JSON.stringify(original))) : 0;
	var log = JSON.parse(fetch('hiker://files/rules/comic/comicLogFile.js'));
	for (var i in log) {
		if (log[i].url == MY_URL) {
			var now = i;
			var order = log[i].order;
			break;
		} else {
			var now = '';
			var order = defaultOrder;
		}
	}
	let orderItem = {
		title: "““””" + '<strong><font color="#098AC1">' + '章节排序⇅' + '</font></strong>',
		url: MY_URL + `@lazyRule=.js:var log = JSON.parse(fetch('hiker://files/rules/comic/comicLogFile.js'));var now='` + now + `';if(now!=''){var now=parseInt(now);var order=log[now].order}else{var now=parseInt(log.length);var order=` + defaultOrder + `;log.push({url:input,order:order});}if(log[now].order){log[now].order=0;}else{log[now].order=1;};writeFile('hiker://files/rules/comic/comicLogFile.js', JSON.stringify(log));refreshPage();'hiker://empty'`,
		col_type: 'text_center_1'
	}
	if (hasLine) {
		orderItem.title = "““””" + '<strong><font color="#098AC1">' + '集数排序⇅' + '</font></strong>';
		orderItem.col_type = "scroll_button"
	}
	let chapterLists = list.map((item)=>({
		title: pdfh(item,"a&&Text"),
		url: pd(item,"a&&href"),
	}));
d.push({
	title: '‘‘’’<strong><font color= "#098AC1">' + "下载漫画" + '</front></strong>',
	url: "hiker://page/download.view#noHistory##noRecordHistory##noRefresh#?rule=本地资源管理",
	col_type: "scroll_button",
	extra: {
		chapterList: chapterLists,
		info:{
			bookName: title,
			ruleName: MY_RULE.title,
			bookTopPic: pic_url,
			parseCode: downloadlazy
		},
		defaultView:"1"
	}
});
	d.push({
		title: '‘‘’’<strong><font color= "#098AC1">' + "本地书架" + '</front></strong>',
		url: "hiker://page/Main.view?rule=本地资源管理",
		col_type: "scroll_button",
	});  
	d.push(orderItem);
	d.push({col_type: 'line'});	
	var list = order ? list : list.reverse();
	if(!list||list==""||deleted==1){
		d.push({
			title: '‘‘’’<strong><font color= "#FF0000">' +'此漫画已下架!!'+ '</front></strong>',
			col_type: 'text_1',
		});
	}
	for (var i in list) {
		try {
			d.push({
				title: pdfh(list[i], chapterTitle),
				url: pd(list[i], chapterUrl) + lazy,
				col_type: pdfh(list[0], chapterTitle).length > 4? 'flex_button' : 'flex_button',
			});
		}catch (e) {}
	}
	d.push({
			col_type: 'big_big_blank_block',
		});
}

function line(lineList, lineTitleRule, varKey) {
	let list = lineList
	varKey = varKey == null ? (MY_RULE.url + '#line-index'): varKey;
	addListener('onClose', $.toString((keyList) => {
		for (let i in keyList) {clearVar(keyList[i])}
	}, [varKey, MY_RULE.title + '#line-title']))
	let line0Title = '';
	try {
		line0Title = pdfh(list[0], lineTitleRule).replace("章节列表：","");
	} catch (e) {}
	log(MY_RULE.title + getVar((MY_RULE.title + '#line-title')))
	for (let i in list) {
		try {
			let lineTitle = pdfh(list[i], lineTitleRule).replace("章节列表：","");
			d.push({
				title: getVar((MY_RULE.title + '#line-title'), line0Title) === lineTitle ? "切换:‘‘" + lineTitle+"":lineTitle+"‘‘:切换",
				url: $().lazyRule((varKey, lineIndex, lineTitle) => {
					putVar(varKey, lineIndex.toString());
					putVar((MY_RULE.title + '#line-title'), lineTitle);
					log(MY_RULE.title + getVar((MY_RULE.title + '#line-title')));
					refreshPage();
					return "toast://已切换至" + lineTitle;
				}, varKey, i, lineTitle),
				col_type: 'scroll_button',
			});
		} catch (e) {}
	}
}

function Simplized(cc){
	var str='',ss=JTPYStr(),tt=FTPYStr();
	for(var i=0;i<cc.length;i++){
		if(cc.charCodeAt(i)>10000&&tt.indexOf(cc.charAt(i))!=-1) str+=ss.charAt(tt.indexOf(cc.charAt(i)));
		else str+=cc.charAt(i);
	}
	return str;
}
