}


function 添加简介(d, 数据源, 页URL, 图片) {
    //获取数据
    var 简介内容 = 数据源
    简介内容 = 简介内容.replace("简介：", "").replace("介绍:", "").replace("漫画", "").replace("……", "").replace("简介", "")

    var 简介内容收;
    if (简介内容.length > 45) {
        简介内容收 = 简介内容.substr(0, 45) + '...'
    } else {
        简介内容收 = 简介内容 + '...'
    }

    //图标获取
    if (!图片) {
        图片 = 'https://lanmeiguojiang.com/tubiao/q/107.png'
    }

    //页URL 获取
    if (!页URL) {
        页URL = MY_RULE.title
    }

    //标题与简介样式
    var mztext = "&nbsp;&nbsp;&nbsp;&nbsp;<small>简介</small><br>"
    var bttitle = '<font color="#098AC1">漫画简介  </font><small><small><font color="#f47983">简介收 ></font></small></small>'

    //展开与收缩操作
    var jj = getMyVar(页URL + "简介收", "已收起");
    var jjurl = $("#noLoading#").lazyRule((简介内容, 简介内容收, 收, 收内容, 当前URL) => {
        var jsbl = 当前URL + "简介收";
        var jj = getMyVar(当前URL + "简介收", "已收起");
        jj = jj == "已收起" ? "展开" : "已收起";
        putMyVar(jsbl, jj)
        updateItem({
            extra: {
                id: 当前URL + "_bt"
            },
            title: 收.replace("简介收", jj)
        })

        updateItem({
            extra: {
                id: 当前URL + "_mz"
            },
            title: jj == "已收起" ? 收内容.replace("简介", 简介内容收) : 收内容.replace("简介", 简介内容)
        })
        return "hiker://empty"
    }, 简介内容, 简介内容收, bttitle, mztext, 页URL)


    //标题
    d.push({
        title: bttitle.replace("简介收", jj),
        url: jjurl,
        col_type: "avatar",
        pic_url: 图片,
        extra: {
            id: 页URL + "_bt"
        }
    });

    //简介
    d.push({
        title: jj == "已收起" ? mztext.replace("简介", 简介内容收) : mztext.replace("简介", 简介内容),
        col_type: "rich_text",
        extra: {
            id: 页URL + "_mz"
        }
    });

}

