function Label_set(d, table_Data) {

    // d：直接填写d即可
    // table_Data：[title:'',list:[title:'',desc:'',url:'',pic:'',type:'',extra:''],.....]

    var color_Selection = ['#519D9E', '#285943', '#098AC1'];
    var ci = 0;
    table_Data.forEach((data, id) => {
        var extra = {
            lineVisible: false,
            name: data.title
        }
        if (data.title.length == 4) {
            data.title = data.title + thrk(15) + '&nbsp;'
        } else if (data.title.length == 5) {
            data.title = data.title + thrk(14) + '&nbsp;'
        } else {
            data.title = data.title + thrk(13)
        }
        d.push({
            title: '““””<br><font color="' + color_Selection[ci] + '">' + data.title + '</font><small><small><font color="#f47983">　更多 ></font></small></small>',
            url: data.url,
            col_type: "text_1",
            extra: extra
        });
        ci = ci == 2 ? 0 : ci + 1
        data.list.forEach((datas) => {
            d.push({
                title: datas.title ? datas.title : '',
                desc: datas.desc ? datas.desc : '',
                url: 'hiker://empty#immersiveTheme##autoCache##noHistory#?url=' + datas.extra.url + '@rule=js:$.require("' + (datas.url ? datas.url : '') + '")',
                pic_url: datas.pic ? datas.pic : '',
                col_type: datas.type ? datas.type : '',
                extra: datas.extra ? datas.extra : ''
            });
        });
    });
}

function 添加列表章节(d, 标题数据, 列表数据, 解析代码, 页URL, 图片) {


    //d与外界d，为同内存地址，不用返回
    //标题数据  即标题名字
    //列表数据必须是JSON  格式是[{title:'',url:'',},.....]  且url必须有前缀

    //图标获取
    if (!图片) {
        图片 = 'https://lanmeiguojiang.com/tubiao/q/18.png'
    }

    //页URL 获取
    if (!页URL) {
        页URL = MY_RULE.title
    }

    //标题名
    var 标题名 = '<b>路线</b>  <small><small><font color=#f9906f>排序 ></font></small></small>'


    var 排序 = getMyVar(页URL + "章节排序", "正序");
    var 提示 = 排序 == '倒序' ? '正序' : '倒序';

    var 排序url = $("#noLoading#").lazyRule((标题名, 标题数据, 列表数据, 解析代码, 页URL) => {
        var 排序 = getMyVar(页URL + "章节排序", "正序");
        var 提示 = 排序 == '倒序' ? '正序' : '倒序';
        updateItem({
            extra: {
                id: 页URL + "_排序"
            },
            title: 标题名.replace('路线', 标题数据).replace('排序', 提示)
        })


        if (提示 == '倒序') {
            列表数据.reverse()
        }
        列表数据.forEach(function(data, ssid) {
            updateItem({
                title: data.title,
                url: data.url + 解析代码,
                extra: {
                    id: 页URL + "_选集" + ssid
                }
            });
        });


        putMyVar(页URL + "章节排序", 提示)
        return "hiker://empty"
    }, 标题名, 标题数据, 列表数据, 解析代码, 页URL)

    d.push({
        title: 标题名.replace('路线', 标题数据).replace('排序', 排序),
        pic_url: 图片,
        url: 排序url,
        col_type: "avatar",
        extra: {
            id: 页URL + "_排序"
        }
    });

    if (排序 == '倒序') {
        列表数据.reverse()
    }

    列表数据.forEach(function(data, id) {
        d.push({
            title: data.title,
            url: data.url + 解析代码,
            col_type: "text_2",
            extra: {
                id: 页URL + "_选集" + id
            }
        });
    });

}

