js:
var d = [];
MY_URL = MY_URL.replace('hiker://empty##', '')
var MY_CODE = request(MY_URL);
//log("共有:" + count + "章")
//代码由吐佬和R佬提供

var html = JSON.parse(MY_CODE).results.comic;

d.push({
    title: html.name + '\n' + html.last_chapter.name,
    desc: html.brief,
    url: html.cover,
    pic: html.cover,
    col_type: 'movie_1_vertical_pic_blur'
})
d.push({
    title: '‘‘’’🗳<font color="#f47983"> 书架</font> ',
    url: "hiker://page/Bookrack.view?rule=本地资源管理&ruleName="+MY_RULE.title,
    col_type: 'text_2'
});


function 解析(pageUrl) {
    let uu = pageUrl || MY_URL;
    var jsons = JSON.parse(request(input, {
        headers: {
            'Cookie': 'webp=0',
            'Referer': uu.replace('#immersiveTheme##autoCache#', ''),
            "User-Agent": 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/87.0.4280.88 Safari/537.36'
        }
    }));
    //log(jsons)    
    var list = jsons.results.chapter.contents;
    var words = jsons.results.chapter.words;
    var pics = [];
    for (var i in words) {               
        list[i].words = parseInt(words[i]);
    }
    //log(list)
    /*
     *  根据某个字段实现对json数组的排序
     * @param   array  要排序的json数组对象
     * @param   field  排序字段（此参数必须为字符串）     
     */
    function jsonSort(array, field) {
        // 数组长度小于2 或 没有指定排序字段 或 不是json格式数据
        if (array.length < 2 || !field || typeof array[0] !== "object") return array;
        // 数字类型排序
        if (typeof array[0][field] === "number") {
            array.sort(function(x, y) {
                return x[field] - y[field]
            });
        }                
        return array;
    }
    pics = jsonSort(list, "words");
    log("解析执行了")
    var picss = [];
    for (var i in pics) {
        picss.push(pics[i].url);
    }   
    return "pics://" + picss.join("&&");
}
let lazy = $("").lazyRule(解析, MY_URL);
let downloadlazy = $.toString(解析);

var doList = [];
var LIST_CODE = request(MY_HOME + '/comicdetail/' + html.path_word + '/chapters', {
    headers: {
        'Cookie': 'webp=0',
        'Referer': MY_URL.replace('#immersiveTheme##autoCache#', ''),
        "User-Agent": 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/87.0.4280.88 Safari/537.36'
    }
});
var data = JSON.parse(LIST_CODE).results;
//log(data)
eval(getCryptoJS())

var key = CryptoJS.enc.Utf8.parse('xxxmanga.woo.key');
var iv = CryptoJS.enc.Utf8.parse(data.substring(0, 16));

data = CryptoJS.enc.Hex.parse(data.substring(16, data.length));
data = CryptoJS.enc.Base64.stringify(data);
data = CryptoJS.AES.decrypt(data, key, {
    "iv": iv,
    "mode": CryptoJS.mode.CBC,
    "padding": CryptoJS.pad.Pkcs7
}).toString(CryptoJS.enc.Utf8).toString()
var list = JSON.parse(data).groups.default.chapters;
for (let i in list) {
    let title = list[i].name
    let url = MY_HOME + '/api/v3/comic/' + html.path_word + '/chapter2/' + list[i].id + '?platform=1&_update=true'
    d.push({
        title: title,
        url: url + lazy,
        col_type: 'text_3'
    })
    doList.push({
        title: title, // 章节名字
        url: url, // 章节url
    });
}

//插入下载按钮
d.splice(1, 0, {
    title: '‘‘’’<strong><font color= "#098AC1">' + "⬇️下载漫画" + '</front></strong>',
    url: "hiker://page/download.view#noRecordHistory##noRefresh#?rule=本地资源管理",
    col_type: "text_2",
    extra: {
        chapterList: doList,
        info: {
            bookName: html.name,
            ruleName: MY_RULE.title,
            bookTopPic: html.cover,
            parseCode: downloadlazy
        },
        defaultView: "1"
    }
});

setResult(d);

