js:
var d = [];
var c=MY_URL.split("/")[3];
var c1=['muou','kbmh','heibaimh','neihan','lianzaimh','guangboju'];
if(getVar('fyAll','0')!=c) {
	clearVar('group_url');
	clearVar('group_index');
}
var true_url=getVar('group_url',MY_URL);log('true_url1:'+true_url);
var html=fetch(true_url,{
	headers:{"content-type": "charst=gb2312"},
	method:'get'});
if(/pageinfo/.test(html)){
	var next_url=true_url.replace(/list.+?html/,'')
		+pdfh(html,'body&&.page&&a:contains(下一页)&&href');
	putVar('group_url',next_url);
}
if(new RegExp(c).test(c1)){
	var group=pdfa(html,'.nav&&dl');
	var index=c1.indexOf(c);
	group=pdfa(group[index],'body&&a');
	if(MY_PAGE==1){
		for (var k=0;k<group.length;k++) {
			d.push({
				title: k==getVar('group_index','-1')
					?'‘‘’’<strong><font color= "#3eaf7c">'
					+pdfh(group[k], 'a&&Text')
					+ '</front></strong>'
					:pdfh(group[k], 'a&&Text'),
				url: (pd(group[k],'a&&href')+'##'+k)+`@lazyRule=.js:
					putVar('group_url',input.split('##')[0]);
					putVar('group_index',input.split('##')[1]);
					refreshPage(false);'hiker://empty'`,
				col_type: 'scroll_button'
			});
		}
	}
}
if(/listl/.test(html)){
	var list=pdfa(html,'body&&.listl&&ul&&li').reverse();
}
else if(/lft clearfix/.test(html)){
	var list=pdfa(html,'body&&.lft.clearfix&&li.clearfix').reverse();
}
else if(/index/.test(true_url)){
	var list=pdfa(html,'body&&.chosenbox').concat(pdfa(html,'body&&.hotbox'));
}
else{
	var list=pdfa(html,'body&&.ulfix&&li');
}
for (var list of list) {
	d.push({
		title: pdfh(list, 'a&&title')?pdfh(list, 'a&&title'):pdfh(list, 'li&&Text'),
		img: pd(list, 'img&&src'),
		url: pd(list, 'a&&href')+`@lazyRule=.js:
			var html=fetch(input, {headers: {"content-type": "charst=gb2312"}});
			var urls=[];
			urls.push({
				url:input,
				options: {headers: {"content-type": "charst=gb2312"}}
			});
			var page=pdfh(html, ".pagination&&a&&Text")
				.replace(/共|页:/g, '');
			for (var k = 2; k <=page; k++) {
				urls.push({
					url: input.replace(".html", "_" + k + ".html"),
					options: {headers: {"content-type": "charst=gb2312"}}
				});
			}
			var pics_tmp=[];
			for(var list of bf(urls)){
				pics_tmp=pics_tmp.concat(pdfa(list,'.article-content&&img'));
			}
			var pics=pics_tmp.map(h=>pdfh(h,'img&&src'));
			'pics://'+pics.join('&&')`,
	});
}
putVar('fyAll',c);
setResult(d);

